import React, { useState } from 'react';
import { Form, Button, Card } from 'react-bootstrap';

const PredictionForm = ({ onSubmit }) => {
  const [ticker, setTicker] = useState('AMZN');
  const [days, setDays] = useState(30);
  const [model, setModel] = useState('lstm');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onSubmit) {
      onSubmit(ticker, days, model);
    }
  };

  const popularStocks = [
    { symbol: 'AMZN', name: 'Amazon' },
    { symbol: 'AAPL', name: 'Apple' },
    { symbol: 'GOOGL', name: 'Google' },
    { symbol: 'MSFT', name: 'Microsoft' },
    { symbol: 'TSLA', name: 'Tesla' },
    { symbol: 'META', name: 'Meta' },
    { symbol: 'NFLX', name: 'Netflix' },
    { symbol: 'NVDA', name: 'NVIDIA' }
  ];

  return (
    <Card className="mb-4">
      <Card.Header>
        <h5 className="mb-0">Prediction Settings</h5>
      </Card.Header>
      <Card.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Stock Symbol</Form.Label>
            <Form.Select 
              value={ticker} 
              onChange={(e) => setTicker(e.target.value)}
              required
            >
              {popularStocks.map(stock => (
                <option key={stock.symbol} value={stock.symbol}>
                  {stock.symbol} - {stock.name}
                </option>
              ))}
            </Form.Select>
            <Form.Text className="text-muted">
              Enter any valid stock ticker symbol
            </Form.Text>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Prediction Model</Form.Label>
            <div>
              <Form.Check
                inline
                type="radio"
                label="LSTM Neural Network"
                name="model"
                value="lstm"
                checked={model === 'lstm'}
                onChange={(e) => setModel(e.target.value)}
                id="model-lstm"
              />
              <Form.Check
                inline
                type="radio"
                label="ARIMA Model"
                name="model"
                value="arima"
                checked={model === 'arima'}
                onChange={(e) => setModel(e.target.value)}
                id="model-arima"
              />
            </div>
            <Form.Text className="text-muted">
              LSTM: Deep learning model | ARIMA: Statistical model
            </Form.Text>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Prediction Days: <strong>{days}</strong></Form.Label>
            <Form.Range 
              min="7" 
              max="90" 
              value={days}
              onChange={(e) => setDays(parseInt(e.target.value))}
            />
            <Form.Text className="text-muted">
              Predict next {days} trading days (7-90 days)
            </Form.Text>
          </Form.Group>

          <Button 
            variant="primary" 
            type="submit" 
            className="w-100"
            size="lg"
          >
            ðŸ“ˆ Predict Stock Price
          </Button>

          <div className="mt-3 text-center">
            <small className="text-muted">
              Predictions are based on historical data and machine learning models
            </small>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default PredictionForm;
