import React from 'react';
import { Card } from 'react-bootstrap';

const StockChart = ({ historical = [], predictions = [] }) => {
  // Safe data extraction
  const safeHistorical = Array.isArray(historical) ? historical : [];
  const safePredictions = Array.isArray(predictions) ? predictions : [];

  if (safeHistorical.length === 0 && safePredictions.length === 0) {
    return (
      <Card>
        <Card.Header>
          <h5 className="mb-0">Stock Price Prediction</h5>
        </Card.Header>
        <Card.Body>
          <div className="text-center py-5">
            <p className="text-muted">No chart data available</p>
          </div>
        </Card.Body>
      </Card>
    );
  }

  // Calculate stats safely
  const getLastHistoricalPrice = () => {
    if (safeHistorical.length === 0) return 0;
    const lastItem = safeHistorical[safeHistorical.length - 1];
    return lastItem?.Close || lastItem?.close || 0;
  };

  const getFirstPredictedPrice = () => {
    if (safePredictions.length === 0) return 0;
    const firstItem = safePredictions[0];
    return firstItem?.Predicted_Close || firstItem?.predicted_close || firstItem?.close || 0;
  };

  const getLastPredictedPrice = () => {
    if (safePredictions.length === 0) return 0;
    const lastItem = safePredictions[safePredictions.length - 1];
    return lastItem?.Predicted_Close || lastItem?.predicted_close || lastItem?.close || 0;
  };

  const lastHistoricalPrice = getLastHistoricalPrice();
  const firstPredictedPrice = getFirstPredictedPrice();
  const lastPredictedPrice = getLastPredictedPrice();

  const changePercent = lastHistoricalPrice > 0 
    ? ((firstPredictedPrice - lastHistoricalPrice) / lastHistoricalPrice * 100)
    : 0;

  return (
    <Card>
      <Card.Header>
        <h5 className="mb-0">Stock Price Prediction</h5>
      </Card.Header>
      <Card.Body>
        {/* Simple chart visualization */}
        <div style={{ 
          height: '300px', 
          background: 'linear-gradient(to bottom, #f8f9fa, #e9ecef)',
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center', 
          borderRadius: '5px', 
          marginBottom: '20px',
          position: 'relative',
          overflow: 'hidden'
        }}>
          <div className="text-center p-4">
            <p className="mb-2">ðŸ“Š Chart Visualization</p>
            <small className="text-muted">
              Install react-chartjs-2 and chart.js for interactive charts
            </small>
          </div>

          {/* Simple line visualization */}
          <div style={{
            position: 'absolute',
            bottom: '20%',
            left: '10%',
            right: '10%',
            height: '2px',
            background: '#007bff',
            opacity: 0.3
          }}></div>
        </div>

        {/* Statistics */}
        <div className="row">
          <div className="col-md-4 mb-3">
            <div className="text-center p-3 border rounded bg-light">
              <h6>Last Historical Price</h6>
              <h4 className="text-primary">${lastHistoricalPrice.toFixed(2)}</h4>
              <small className="text-muted">Historical data points: {safeHistorical.length}</small>
            </div>
          </div>
          <div className="col-md-4 mb-3">
            <div className="text-center p-3 border rounded bg-light">
              <h6>First Prediction</h6>
              <h4 className={changePercent >= 0 ? 'text-success' : 'text-danger'}>
                ${firstPredictedPrice.toFixed(2)}
              </h4>
              <small className={changePercent >= 0 ? 'text-success' : 'text-danger'}>
                {changePercent >= 0 ? 'â†—' : 'â†˜'} {Math.abs(changePercent).toFixed(2)}%
              </small>
            </div>
          </div>
          <div className="col-md-4 mb-3">
            <div className="text-center p-3 border rounded bg-light">
              <h6>Last Prediction</h6>
              <h4 className="text-info">${lastPredictedPrice.toFixed(2)}</h4>
              <small className="text-muted">Prediction points: {safePredictions.length}</small>
            </div>
          </div>
        </div>

        {/* Data Summary */}
        <div className="mt-4 p-3 border rounded">
          <h6>ðŸ“ˆ Data Summary</h6>
          <div className="row mt-2">
            <div className="col-md-6">
              <p><strong>Historical Period:</strong> {safeHistorical.length} days</p>
              <p><strong>Minimum Price:</strong> $
                {safeHistorical.length > 0 
                  ? Math.min(...safeHistorical.map(h => h.Close || h.close || 0)).toFixed(2) 
                  : '0.00'
                }
              </p>
              <p><strong>Maximum Price:</strong> $
                {safeHistorical.length > 0 
                  ? Math.max(...safeHistorical.map(h => h.Close || h.close || 0)).toFixed(2) 
                  : '0.00'
                }
              </p>
            </div>
            <div className="col-md-6">
              <p><strong>Prediction Period:</strong> {safePredictions.length} days</p>
              <p><strong>Average Prediction:</strong> $
                {safePredictions.length > 0 
                  ? (safePredictions.reduce((sum, p) => sum + (p.Predicted_Close || p.predicted_close || 0), 0) / safePredictions.length).toFixed(2) 
                  : '0.00'
                }
              </p>
              <p><strong>Total Change:</strong> 
                <span className={lastPredictedPrice >= lastHistoricalPrice ? 'text-success' : 'text-danger'}>
                  {((lastPredictedPrice - lastHistoricalPrice) / lastHistoricalPrice * 100).toFixed(2)}%
                </span>
              </p>
            </div>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

export default StockChart;
